+export function initIndustrialSlider() {
+    // Add styles
+    const styleSheet = document.createElement('style');
+    styleSheet.textContent = industrialSliderStyles + additionalStyles + stickyStyles;
+    document.head.appendChild(styleSheet);
+
+    // Insert slider content
+    const temp = document.createElement('div');
+    temp.innerHTML = industrialSliderContent;
+    
+    const ballSection = document.getElementById('ball');
+    if (ballSection && ballSection.parentNode) {
+        ballSection.parentNode.insertBefore(temp.firstElementChild, ballSection.nextSibling);
+    } else {
+        document.body.appendChild(temp.firstElementChild);
+    }
+
+    // Initialize Swiper
+    const swiper = new Swiper('.mn_slr', {
+        slidesPerView: 'auto',
+        spaceBetween: 30,
+        speed: 1200,
+        grabCursor: true,
+        watchSlidesProgress: true,
+        mousewheelControl: false,
+        keyboardControl: true,
+        centeredSlides: false,
+        initialSlide: 0,
+        slideToClickedSlide: true,
+        allowTouchMove: true,
+        navigation: {
+            nextEl: '.s_next',
+            prevEl: '.s_prev'
+        },
+        on: {
+            init: function() {
+                updateSlides(this);
+                updateProgress(this);
+            },
+            slideChange: function() {
+                updateSlides(this);
+                updateProgress(this);
+            }
+        }
+    });
+
+    // Lenis scroll handling
+    const sliderContainer = document.querySelector('.slider-container');
+    const slider = document.querySelector('.s-industrial-slider');
+    const progressBar = document.querySelector('.slider-progress-bar');
+    const totalSlides = swiper.slides.length;
+    let currentSection = 0;
+    let isAnimating = false;
+
+    // Get Lenis instance that was already initialized
+    const lenis = window.lenis;
+
+    if (lenis) {
+        lenis.on('scroll', ({ scroll, limit, velocity, direction, progress }) => {
+            const sliderRect = sliderContainer.getBoundingClientRect();
+            const containerHeight = sliderContainer.offsetHeight;
+            const viewportHeight = window.innerHeight;
+            const scrollProgress = (scroll - sliderRect.top) / (containerHeight - viewportHeight);
+            
+            // Calculate which section we're in based on scroll progress
+            if (sliderRect.top <= 0 && sliderRect.bottom >= viewportHeight) {
+                const targetSlide = Math.floor(scrollProgress * totalSlides);
+                
+                if (!isAnimating && targetSlide !== currentSection && targetSlide >= 0 && targetSlide < totalSlides) {
+                    isAnimating = true;
+                    currentSection = targetSlide;
+                    swiper.slideTo(targetSlide, 1200, true);
+                    
+                    setTimeout(() => {
+                        isAnimating = false;
+                    }, 1200);
+                }
+
+                // Update progress bar
+                const progressPercentage = (scrollProgress * 100);
+                progressBar.style.height = `${Math.min(100, Math.max(0, progressPercentage))}%`;
+                
+                // Add visible class when in view
+                slider.classList.add('is-visible');
+            } else {
+                slider.classList.remove('is-visible');
+            }
+        });
+    }
+
+    // Handle slide updates
+    function updateSlides(swiper) {
+        const slides = swiper.slides;
+        slides.forEach((slide, index) => {
+            const overlay = slide.querySelector('.ovr');
+            const caption = slide.querySelector('.s_ttl');
+            
+            if (index === swiper.activeIndex) {
+                slide.style.transition = 'transform 1.2s ease-out';
+                slide.style.transform = 'scale(1)';
+                if (overlay) {
+                    overlay.style.transition = 'opacity 1.2s ease-out';
+                    overlay.style.opacity = '0';
+                }
+                if (caption) {
+                    caption.style.transition = 'all 1.2s ease-out';
+                    caption.style.opacity = '1';
+                    caption.style.transform = 'translateY(0)';
+                }
+            } else {
+                slide.style.transition = 'transform 1.2s ease-out';
+                slide.style.transform = 'scale(0.85)';
+                if (overlay) {
+                    overlay.style.transition = 'opacity 1.2s ease-out';
+                    overlay.style.opacity = '1';
+                }
+                if (caption) {
+                    caption.style.transition = 'all 1.2s ease-out';
+                    caption.style.opacity = '0';
+                    caption.style.transform = 'translateY(20px)';
