+// industrial-slider.js
+import Swiper from 'swiper';
+import { Navigation, EffectFade, EffectCreative, Autoplay } from 'swiper/modules';
+import 'swiper/css';
+import 'swiper/css/navigation';
+import 'swiper/css/effect-creative';
+
+// Initialize Swiper with additional modules
+Swiper.use([Navigation, EffectFade, EffectCreative, Autoplay]);
+
+// Add additional styles for sticky behavior
+const stickyStyles = `
+    .s-industrial-slider {
+        position: relative;
+        z-index: 1;
+        height: 100vh; /* Full viewport height */
+    }
+
+    .s-industrial-slider.is-sticky {
+        position: sticky;
+        top: 0;
+        left: 0;
+        right: 0;
+    }
+
+    .slider-container {
+        height: 300vh; /* 3x viewport height for 3 slides */
+        position: relative;
+    }
+
+    .mn_slr_wrap {
+        position: relative;
+        overflow: hidden;
+        z-index: 10;
+        background: #000;
+        height: 100vh;
+    }
+`;
