// industrial-slider.js

export const industrialSliderContent = `
<section id="industrial-showcase" class="s-industrial-slider">
    <div class="mn_slr_wrap">
        <div class="swiper mn_slr">
            <div class="swiper-wrapper">
                <div class="swiper-slide">
                    <div class="caption">
                        <img src="/assets/lighting-2.png" alt="Industrial project showcase">
                        <div class="s_ttl">
                            <div class="h4">Свои строительные <span class="br">бригады</span></div>
                            <p>Проверенные профессионалы</p>
                        </div>
                        <div class="ovr"></div>
                    </div>
                </div>
                <div class="swiper-slide">
                    <div class="caption">
                        <img src="/assets/solar-5.png" alt="Industrial project showcase">
                        <div class="s_ttl">
                            <div class="h4">Полная проверка <span class="br">комплектующих</span></div>
                            <p>Комплексная проверка всех комплектующих.</p>
                        </div>
                        <div class="ovr"></div>
                    </div>
                </div>
                <div class="swiper-slide">
                    <div class="caption">
                        <img src="/assets/industrial-3.png" alt="Industrial project showcase">
                        <div class="s_ttl">
                            <div class="h4">Свои строительные <span class="br">бригады</span></div>
                            <p>Проверенные профессионалы</p>
                        </div>
                        <div class="ovr"></div>
                    </div>
                </div>
            </div>
        </div>
        <div class="s_nav">
            <div class="s_prev"><span></span></div>
            <div class="s_next"><span></span></div>
        </div>
    </div>
</section>
`;

const industrialSliderStyles = `
    .s-industrial-slider {
        position: relative;
        z-index: 1;
        background: #0f1318;
    }

    .mn_slr_wrap {
        position: relative;
        overflow: hidden;
        z-index: 10;
    }

    .mn_slr {
        height: 36.51vw;
        padding-left: 25.26vw;
    }

    .swiper-slide {
        width: 56.719vw;
    }

    .caption {
        position: relative;
        height: 100%;
        overflow: hidden;
    }

    .caption img {
        position: absolute;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        margin: auto;
        object-fit: cover;
        object-position: 50% 50%;
        width: 100%;
        height: 100%;
        transform-origin: 0 0;
        transform: scale(0.85);
        transition: 1.4s;
    }

    .swiper-slide-active .caption img {
        transform: scale(1);
    }

    .s_ttl {
        position: relative;
        z-index: 20;
        padding: 2.917vw 2.76vw;
    }

    .h4 {
        color: #fff;
        font-size: 1.875vw;
        line-height: 2.135vw;
        letter-spacing: -0.047vw;
    }

    .s_ttl p {
        color: rgba(255,255,255,0.6);
        font-size: 0.833vw;
        line-height: 2.135vw;
        letter-spacing: 0.008vw;
        margin: 1.927vw 0 0;
    }

    .ovr {
        position: absolute;
        top: 0;
        left: 0;
        bottom: 0;
        right: 0;
        margin: auto;
        width: 100%;
        height: 100%;
        background: #0f1318;
        transform-origin: 0 50%;
        z-index: 20;
        transform: scale(1, 1);
        transition: transform 0.7s ease-in-out;
    }

    .s_nav {
        position: relative;
        width: 7.083vw;
        height: 3.385vw;
        display: flex;
        justify-content: space-between;
        margin-top: 2vw;
        margin-left: 28.021vw;
    }
`;

const additionalStyles = `
    .s_prev, .s_next {
        position: relative;
        width: 3.385vw;
        height: 100%;
        background: transparent;
        border-radius: 100%;
        border: 0.052vw solid #fff;
        transition: 0.35s;
        cursor: pointer;
    }

