@@ -0,0 +1,156 @@
+// src/components/splash.js
+export const splashContent = `
+<section id="splash" data-w-id="splash-section" class="radiant-splash s-splash">
+    <div class="splash-inner">
+        <div class="huge-statement-wrapper">
+            <div class="huge-statement">
+                <div class="content-wrapper">
+                    <h1 class="huge-text">Illuminating Tomorrow's <br>Energy Solutions</h1>
+                    <div class="interactive-text-wrapper">
+                        <p class="interactive-text paragraph white">
+                            Pioneering excellence in 
+                            <span class="highlight-word" data-category="lighting">lighting</span>, 
+                            <span class="highlight-word" data-category="solar">solar</span>, and
+                            <span class="highlight-word" data-category="transformers">transformers</span>
+                            technologies.
+                        </p>
+                    </div>
+                </div>
+            </div>
+        </div>
+    </div>
+
+    <div class="hover-images">
+        <div class="hover-image-group" data-category="lighting">
+            <div class="hover-image">
+                <img src="/assets/lighting-1.jpg" alt="Lighting solution 1">
+            </div>
+            <div class="hover-image">
+                <img src="/assets/lighting-2.jpg" alt="Lighting solution 2">
+            </div>
+            <div class="hover-image">
+                <img src="/assets/lighting-3.jpg" alt="Lighting solution 3">
+            </div>
+        </div>
+        <div class="hover-image-group" data-category="solar">
+            <div class="hover-image">
+                <img src="/assets/solar-1.jpg" alt="Solar solution 1">
+            </div>
+            <div class="hover-image">
+                <img src="/assets/solar-2.jpg" alt="Solar solution 2">
+            </div>
+            <div class="hover-image">
+                <img src="/assets/solar-3.jpg" alt="Solar solution 3">
+            </div>
+        </div>
+        <div class="hover-image-group" data-category="transformers">
+            <div class="hover-image">
+                <img src="/assets/transformer-1.jpg" alt="Transformer solution 1">
+            </div>
+            <div class="hover-image">
+                <img src="/assets/transformer-2.jpg" alt="Transformer solution 2">
+            </div>
+            <div class="hover-image">
+                <img src="/assets/transformer-3.jpg" alt="Transformer solution 3">
+            </div>
+        </div>
+    </div>
+
+    <div class="lines-wrapper">
+        <div class="line"></div>
+        <div class="line"></div>
+        <div class="line"></div>
+        <div class="line"></div>
+        <div class="line"></div>
+    </div>
+</section>
+`;
+
+export function initSplash() {
+    if (!document.getElementById('splash-content')) {
+        console.error('Splash content container not found');
+        return;
+    }
+
+    // Insert content
+    document.getElementById('splash-content').innerHTML = splashContent;
+
+    // Get elements
+    const words = document.querySelectorAll('.highlight-word');
+    const imageGroups = document.querySelectorAll('.hover-image-group');
+
+    // Set initial states
+    imageGroups.forEach(group => {
+        group.style.opacity = '0';
+        group.style.visibility = 'hidden';
+    });
+
+    // Handle hover interactions with vanilla JS animations
+    words.forEach(word => {
+        const category = word.dataset.category;
+        const imageGroup = document.querySelector(`.hover-image-group[data-category="${category}"]`);
+        const images = imageGroup?.querySelectorAll('.hover-image');
+
+        if (!imageGroup || !images) return;
+
+        word.addEventListener('mouseenter', () => {
+            // Show image group
+            imageGroup.style.visibility = 'visible';
+            imageGroup.style.opacity = '1';
+
+            // Animate word
+            word.style.color = '#00e5d2';
+            
+            // Animate images
+            images.forEach((img, index) => {
+                setTimeout(() => {
+                    img.style.transform = 'translateY(0) scale(1)';
+                    img.style.opacity = '1';
+                }, index * 100);
+            });
+        });
+
+        word.addEventListener('mouseleave', () => {
+            // Hide image group
+            imageGroup.style.opacity = '0';
+            setTimeout(() => {
+                imageGroup.style.visibility = 'hidden';
+            }, 300);
+
+            // Reset word
+            word.style.color = 'white';
+            
+            // Reset images
+            images.forEach((img, index) => {
+                setTimeout(() => {
+                    img.style.transform = 'translateY(20px) scale(0.95)';
+                    img.style.opacity = '0';
+                }, index * 50);
+            });
+        });
+    });
+
+    // Add entrance animation
+    const title = document.querySelector('.huge-text');
+    const text = document.querySelector('.interactive-text');
+
+    if (title) {
+        title.style.opacity = '0';
+        title.style.transform = 'translateY(50px)';
+        setTimeout(() => {
+            title.style.transition = 'all 1s cubic-bezier(0.16, 1, 0.3, 1)';
+            title.style.opacity = '1';
+            title.style.transform = 'translateY(0)';
+        }, 100);
+    }
+
+    if (text) {
+        text.style.opacity = '0';
+        text.style.transform = 'translateY(30px)';
+        setTimeout(() => {
+            text.style.transition = 'all 1s cubic-bezier(0.16, 1, 0.3, 1)';
+            text.style.opacity = '1';
+            text.style.transform = 'translateY(0)';
+        }, 600);
+    }
+}
