+// src/components/splash.js
+export const splashContent = `
+<section id="splash" data-w-id="splash-section" class="radiant-splash s-splash">
+    <div class="splash-inner">
+        <div class="huge-statement-wrapper">
+            <div class="huge-statement">
+                <div class="content-wrapper">
+                    <h1 class="huge-text">Illuminating Tomorrow's <br>Energy Solutions</h1>
+                    <div class="interactive-text-wrapper">
+                        <p class="interactive-text paragraph white">
+                            Pioneering excellence in 
+                            <span class="highlight-word" data-category="lighting">lighting</span>, 
+                            <span class="highlight-word" data-category="solar">solar</span>, and
+                            <span class="highlight-word" data-category="transformers">transformers</span>
+                            technologies.
+                        </p>
+                    </div>
+                </div>
+            </div>
+        </div>
+    </div>
+
+    <div class="hover-images-container">
+        <div class="hover-images-group" data-category="lighting">
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?lighting,industrial" alt="Lighting" class="hover-image">
+            </div>
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?lighting,modern" alt="Lighting" class="hover-image">
+            </div>
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?lighting,commercial" alt="Lighting" class="hover-image">
+            </div>
+        </div>
+
+        <div class="hover-images-group" data-category="solar">
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?solar,panel" alt="Solar" class="hover-image">
+            </div>
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?solar,energy" alt="Solar" class="hover-image">
+            </div>
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?solar,power" alt="Solar" class="hover-image">
+            </div>
+        </div>
+
+        <div class="hover-images-group" data-category="transformers">
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?transformer,electric" alt="Transformers" class="hover-image">
+            </div>
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?transformer,power" alt="Transformers" class="hover-image">
+            </div>
+            <div class="hover-image-wrapper">
+                <img src="https://source.unsplash.com/800x600/?transformer,industrial" alt="Transformers" class="hover-image">
+            </div>
+        </div>
+    </div>
+</section>
+`;
+
+export function initSplash() {
+    // Insert content
+    const splashContainer = document.getElementById('splash-content');
+    if (!splashContainer) {
+        console.error('Splash content container not found');
+        return;
+    }
+    splashContainer.innerHTML = splashContent;
+
+    // Setup hover interactions
+    const words = document.querySelectorAll('.highlight-word');
+    let activeGroup = null;
+
+    words.forEach(word => {
+        const category = word.dataset.category;
+        const imageGroup = document.querySelector(`.hover-images-group[data-category="${category}"]`);
+        
+        if (!imageGroup) {
+            console.warn(`Image group for category ${category} not found`);
+            return;
+        }
+
+        // Handle mouse enter
+        word.addEventListener('mouseenter', () => {
+            // Hide previously active group
+            if (activeGroup) {
+                activeGroup.classList.remove('active');
+            }
+            
+            // Show current group
+            imageGroup.classList.add('active');
+            activeGroup = imageGroup;
+            
+            // Add hover state to word
+            word.classList.add('hover');
+            
+            console.log(`Hovering over ${category}`); // Debug log
+        });
+
+        // Handle mouse leave
+        word.addEventListener('mouseleave', () => {
+            // Only hide if moving out of the word (not to images)
+            setTimeout(() => {
+                if (!word.matches(':hover')) {
+                    imageGroup.classList.remove('active');
+                    word.classList.remove('hover');
+                    activeGroup = null;
+                }
+            }, 100);
+            
+            console.log(`Left ${category}`); // Debug log
+        });
+    });
+
+    // Add entrance animation
+    const title = document.querySelector('.huge-text');
+    const text = document.querySelector('.interactive-text');
+
+    requestAnimationFrame(() => {
+        title?.classList.add('animate-in');
+        setTimeout(() => {
+            text?.classList.add('animate-in');
+        }, 500);
+    });
+}
