// src/components/splash.js
export const splashContent = `
<section id="splash" data-w-id="splash-section" class="s-splash">
    <div class="splash-trigger-1"></div>
    <div class="splash-trigger-2"></div>
    <div class="splash-trigger-3"></div>
    <div class="splash-trigger-4"></div>

    <div class="gray-shape-wrapper">
        <div class="gray-shape-sticky">
            <div class="gray-shape-opacity">
                <img src="/assets/circle.svg" loading="lazy" alt="" class="gray-shape">
            </div>
        </div>
    </div>

    <div class="splash-inner">
        <div id="w-node-div-block-21-7d08bc7f" class="huge-statement-wrapper">
            <div class="huge-statement">
                <div class="content-wrapper">
                    <h1 class="huge-text">Illuminating Tomorrow's <br>Energy Solutions</h1>
                    <div class="interactive-text-wrapper">
                        <p class="interactive-text paragraph white">
                            Pioneering excellence in 
                            <span class="highlight-word" data-category="lighting">lighting</span>, 
                            <span class="highlight-word" data-category="solar">solar</span>, and
                            <span class="highlight-word" data-category="transformers">transformers</span>
                            technologies.
                        </p>
                    </div>
                </div>
            </div>
        </div>
    </div>

    <!-- Image Arrays Container -->
    <div class="image-arrays-container">
        <div class="image-array lighting-array">
            <div class="image-array-inner">
                <img src="/assets/lighting-1.jpg" alt="" class="array-image">
                <img src="/assets/lighting-2.jpg" alt="" class="array-image">
                <img src="/assets/lighting-3.jpg" alt="" class="array-image">
                <img src="/assets/lighting-4.jpg" alt="" class="array-image">
                <img src="/assets/lighting-5.jpg" alt="" class="array-image">
            </div>
        </div>
        <div class="image-array solar-array">
            <div class="image-array-inner">
                <img src="/assets/solar-1.jpg" alt="" class="array-image">
                <img src="/assets/solar-2.jpg" alt="" class="array-image">
                <img src="/assets/solar-3.jpg" alt="" class="array-image">
                <img src="/assets/solar-4.jpg" alt="" class="array-image">
                <img src="/assets/solar-5.jpg" alt="" class="array-image">
            </div>
        </div>
        <div class="image-array transformers-array">
            <div class="image-array-inner">
                <img src="/assets/transformer-1.jpg" alt="" class="array-image">
                <img src="/assets/transformer-2.jpg" alt="" class="array-image">
                <img src="/assets/transformer-3.jpg" alt="" class="array-image">
                <img src="/assets/transformer-4.jpg" alt="" class="array-image">
                <img src="/assets/transformer-5.jpg" alt="" class="array-image">
            </div>
        </div>
    </div>

    <div class="splash-gradient-absolute">
        <div class="splash-gradient-sticky">
            <div class="splash-gradient-inner">
                <div class="splash-gradient-1"></div>
                <div class="splash-gradient-2"></div>
                <div class="splash-gradient-3"></div>
                <div class="splash-gradient-4"></div>
            </div>
        </div>
        <div class="grain-overlay-splash"></div>
    </div>
</section>
`;

export function initSplash(gsap) {
    document.getElementById('splash-content').innerHTML = splashContent;

    // Initialize highlight words interaction
    const highlightWords = document.querySelectorAll('.highlight-word');
    const imageArrays = {
        lighting: document.querySelector('.lighting-array'),
        solar: document.querySelector('.solar-array'),
        transformers: document.querySelector('.transformers-array')
    };

    // Create hover animations
    highlightWords.forEach(word => {
        const category = word.dataset.category;
        const imageArray = imageArrays[category];

        // Initial state
        gsap.set(imageArray, { autoAlpha: 0 });
        gsap.set(imageArray.querySelectorAll('.array-image'), { 
            y: 100,
            opacity: 0,
            rotateX: -30
        });

        // Hover animations
        word.addEventListener('mouseenter', () => {
            // Show image array
            gsap.to(imageArray, {
                autoAlpha: 1,
                duration: 0.4
            });

            // Animate images
            gsap.to(imageArray.querySelectorAll('.array-image'), {
                y: 0,
                opacity: 1,
                rotateX: 0,
                stagger: 0.1,
                duration: 0.8,
                ease: "power3.out"
            });

            // Highlight word
            gsap.to(word, {
                color: "#00e5d2",
                duration: 0.3
            });
        });

        word.addEventListener('mouseleave', () => {
            // Hide image array
            gsap.to(imageArray, {
                autoAlpha: 0,
                duration: 0.4
            });

            // Reset images
            gsap.to(imageArray.querySelectorAll('.array-image'), {
                y: 100,
                opacity: 0,
                rotateX: -30,
                stagger: 0.05,
                duration: 0.4,
                ease: "power2.in"
            });

            // Reset word
            gsap.to(word, {
                color: "white",
                duration: 0.3
            });
        });
    });

    // Initial animations
    gsap.from('.huge-text', {
        y: 100,
        opacity: 0,
        duration: 1.2,
        ease: "power3.out"
    });

    gsap.from('.interactive-text', {
        y: 50,
        opacity: 0,
        duration: 1,
        delay: 0.5,
        ease: "power3.out"
    });
}
